body {
    text-align:center;
    font-family:Verdana, Arial, Helvetica, sans-serif;
    font-size:.7em;
    padding:10px;
    margin:0px;
    color:#999999 !important;
    background:#181818 !important;
    line-height:1.3em;
}

#main-content {
 margin-bottom: 10px !important; 
}

a {
    color:#cccccc !important;
    text-decoration:none;
    border-bottom: 0px !important; 
    text-shadow:none !important;
    transition:text-shadow 1s, color 1s !important;
}

a:hover {
    color:#eee !important;
    text-decoration:none !important;
    text-shadow:0 0 5px #fff !important;
    transition:text-shadow 1s, color 1s !important; 
}

#TPBlogo {
    float:left;
    border:0;
    padding:0;
    margin-top: -7px !important; 
    padding-right:10px;
    margin-bottom: 3px !important; 
    transform: scale(.9) !important; 
    transition: transform .4s;
}
                       
#fp h1 a {
    transform: scale(.9) !important; 
    transition: transform .4s !important;
}

#TPBlogo:hover, #fp h1 a:hover {
   transform: scale(1) !important; 
   transition: transform .4s !important;
}

#breadcrumbs {
    text-align:left;
    vertical-align:bottom;
    font-size:0.8em;
    font-family:Tahoma, Arial, Verdana, sans-serif;
    font-weight:bold;
    padding:0 0 0 5px;
    margin:0;
    clear:both;
}

h2 {
    font-size:1.1em;
    line-height:1.5em;
    text-align:right;
    padding:2px 5px;
    margin:0;
    border-bottom: 0px solid #333333 !important;
    background: #282828 !important;
    font-weight:normal;
    color:#aaa !important;
    clear:both;
    border-radius: 5px !important;
}

h2 span {
    font-weight:bold;
    float:left;
    font-size:1.1em;
}

.header a {
    color:#cccccc !important;
}

p {
   color: #999999 !important; 
}

.searchBox, input {
 background-color: #333333 !important; 
 color: #aaa !important; 
 border: 1px solid #444444 !important; 
 border-radius: 5px !important; 
}

/* Search */

#inp > input:nth-child(1), .inputbox  {
 -moz-appearance: none !important; 
 background: linear-gradient(to top, #333, #181818) !important; 
 color: #aaa !important; 
 border: 1px solid #555 !important; 
 border-radius: 5px !important; 
 box-shadow: 0 0 10px transparent !important; 
 transition: box-shadow 1s !important; 
}

#inp > input:nth-child(1):focus, .inputbox:focus {
 box-shadow: 0 0 10px #666666 !important; 
 transition: box-shadow 1s !important; 
}

/* Button */

.submitbutton, #category, #subm > input {
 -moz-appearance: none !important; 
 background: linear-gradient(#444444, #282828) !important; 
 border: 1px solid #555 !important; 
 color: #aaa !important; 
 border-radius: 5px !important; 
 box-shadow: 0 0 10px transparent !important; 
 transition: box-shadow 1s !important; 
}

.submitbutton:hover, #category:hover, #subm > input:hover {
 -moz-appearance: none !important; 
 border: 1px solid #666666 !important; 
 background: linear-gradient(#555555, #333333) !important; 
 box-shadow: 0 0 10px #666666 !important; 
 transition: box-shadow 1s !important; 
}


#searchfield {
    display:block;
    width:650px;
    height:200px;
    margin:0 auto;
    text-align:center;
    vertical-align:bottom;
    padding:0;
}

#q {
    vertical-align:bottom;
    text-align:left;
    margin:0;
}

#q .inputbox {
    margin:6px;
    width:300px;
    vertical-align:middle;
    padding:2px; 
}

.submitbutton {
    vertical-align:middle;
}

.nohover:hover {
    background:#282828 !important;
}

#main-content {
    background:#282828 !important;
    border-radius: 5px !important; 
    padding-right: 0 !important; 
}

 #details, #detailsframe #title, #detailsframe  {
   background: #282828 !important;
   border: 0px none !important; 
}

#details dt, #details dd {
 border-color: #282828 !important; 
}

 font {
    color:#999999 !important;
}


thead#tableHead th {
    background: #333 !important;
    text-align: center !important; 
}

table#searchResult th, table#searchResult td {
    padding: 4px 10px !important;
    border: 0px !important; 
}

table#searchResult tr, .nfo {
    background: #282828 !important;
}

 .nfo, #comments .comment {
   background: #333333 !important;
   border: 1px solid #555555 !important;
   border-radius: 5px !important; 
   color: #aaa !important;
}

#comments .comment {
   padding: 2px 4px !important; 
}


table#searchResult tr.alt {
    background: #333333 !important;
}

table#searchResult tr:hover {
    background:#444444 !important;
}

table#searchResult td.vertTh {
    color:#999999 !important;
}

table#searchResult img {
    border:0;
    margin-left:2px;
    margin-right:2px;
}

table#searchResult img.dl {
    width:9px;
    height:11px;
}

table#searchResult img.co {
    width:11px;
    height:9px;
}

/* ## UPLOAD. PHP ## */
.uldetails label {
    font-weight:bold;
    color:#999999 !important;
}

.uldetails td {
    width:120px;
    text-align:left;
    font-weight:bold;
    vertical-align:top;
    padding:7px 0px 7px 0;
    margin:0;
    color:#999999 !important;
    border-bottom:1px dashed #444444 !important;
}

.uldetails .tdleft {
    width:100px;
    text-align:left;
    padding-right:10px;
    border-bottom:1px dashed #E8DCD2;
}

/* ## END OF UPLOAD. PHP ## */
#browseContainer {
    text-align:center;
    font-family:Verdana, Helvetica;
    font-size:1em;
}

#browseContainer a {
    color:#009;
    text-decoration:none;
}

#browseContainer a:hover {
    border-bottom:1px solid #009;
}

#browseContainer dt {
    font-weight:bold;
    font-size:1.6em;
    margin-bottom:5px;
    clear:both;
}

.tv #browseContainer dt {
    line-height:1.4em;
    font-size:1.3em;
}

.tv #browseContainer dd {
    line-height:1.4em;
}

.tv #browseContainer .categoriesContainer {
    width:33%;
}

#browseContainer dd {
    margin:0 0 15px 10px;
    float:left;
    font-size:1.2em;
}

#browseContainer .categoriesContainer {
    text-align:left;
    vertical-align:top;
    padding:15px;
    width:50%;
}

* html .categoriesContainer a {
    display:inline-block;
}

dt#upload a {
    color:#080;
}

dt#upload a:hover {
    border-bottom:1px solid #080;
}

dt#latest a {
    color:#080;
}

dt#latest a:hover {
    border-bottom:1px solid #080;
}

dt#showall a {
